let circleZoomMax = 18;
let circleZoomMin = 10;
import regeneratorRuntime from 'regenerator-runtime';
let map;
function loadMap(coords) {
  map = L.map('map', {
    minZoom: circleZoomMin,
    maxZoom: circleZoomMax,
  }).setView(coords, 15);

  L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
    attribution:
      '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors',
  }).addTo(map);
}
function init() {
  loadMap([46.5547, 15.6459]);
}

const getData = async function (city) {
  try {
    const response = await fetch(
      `http://api.positionstack.com/v1/forward?access_key=32695ca2d7bda0ff957097a146fe53bc&query=${city}`
    );
    const data = response.json();
    return data;
  } catch (error) {}
};

init();
let searced = false;
const inputSearch = document.querySelector('.citySearch-input');
const searchCityButton = document.querySelector('.btn-citySearch');
searchCityButton.addEventListener('click', async function () {
  const data = await getData(inputSearch.value);
  console.log(data);
  searced = true;
  map.setView([data.data[0].latitude, data.data[0].longitude], 15);
  searced != true;
  inputSearch.value = '';
});

const canvas = document.querySelector('#myCanvas');
const mapP = document.querySelector('#map');
const drawButton = document.querySelector('.draw');
const undrawButton = document.querySelector('.undraw');
undrawButton.addEventListener('click', function () {
  canvas.style.zIndex = 1;
  mapP.style.zIndex = 1;
  addTextBoolean = false;
});

let addTextBoolean = false;

let lat, lng;
let position;
let positionOnMap;
let isDrawing = false;
let ctx = canvas.getContext('2d');
drawButton.addEventListener('click', function () {
  canvas.style.zIndex = 2;
  mapP.style.zIndex = -1;

  //getMouse position

  position = {
    x: 0,
    y: 0,
  };
  const yea = map.getPixelBounds();
  console.log(yea);
  positionOnMap = {
    position: [{ positionX: 0, positionY: 0 }],
  };
  const getMousePosition = function (e) {
    let xP = e.clientX;
    let yP = e.clientY;
    position.x = xP;
    position.y = yP;
  };
  const changeColor = document.querySelectorAll('.changeColor');
  let color = 'red';
  ctx.strokeStyle = 'red';
  changeColor.forEach((el) =>
    el.addEventListener('click', function (e) {
      ctx.strokeStyle = e.target.dataset.color;
      color = e.target.dataset.color;
    })
  );
  const mouseDraw = function (e) {
    if (!isDrawing) return;

    ctx.lineCap = 'round';

    color = ctx.strokeStyle;
    /*
  blueColor.addEventListener("click", function (e) {
    console.log(e.target.dataset.color);
    ctx.strokeStyle = e.target.dataset.color;
  }),
  */
    //ctx.moveTo(position.x, position.y);
    getMousePosition(e);
    //ctx.lineTo(position.x, position.y);
    let zoom = map.getZoom();
    let desiredRadius;

    switch (zoom) {
      case 18:
        desiredRadius = 20;

        break;
      case 17:
        desiredRadius = 40;

        break;
      case 16:
        desiredRadius = 60;

        break;
      case 15:
        desiredRadius = 80;
      case 14:
        desiredRadius = 100;

        break;
      case 13:
        desiredRadius = 200;

        break;
      case 12:
        desiredRadius = 400;

        break;
      case 11:
        desiredRadius = 600;

        break;
      case 10:
        desiredRadius = 1200;

        break;

      default:
        break;
    }

    let { lat, lng } = map.containerPointToLatLng([position.x, position.y]);
    positionOnMap.position.positionX = lat;
    positionOnMap.position.positionY = lng;

    L.circle(
      [positionOnMap.position.positionX, positionOnMap.position.positionY],
      {
        color: color,
        radius: desiredRadius,
        opacity: 0.5,
        fillOpacity: 1,
      }
    ).addTo(map);

    //console.log(position);
    //console.log(positionOnMap);
    let yea = map.latLngToLayerPoint([lat, lng]);
    let xCoords = yea.x;
    let yCoords = yea.y;
    console.log(position);
    console.log(positionOnMap.position);
  };

  function startDrawing(e) {
    isDrawing = true;
    getMousePosition(e);
  }
  function stopDrawing() {
    isDrawing = false;
  }
  canvas.addEventListener('mousedown', startDrawing);
  canvas.addEventListener('mousemove', mouseDraw);
  canvas.addEventListener('mouseup', stopDrawing);
});

